// Fruit Cultivation - automatic acting

if (itemRegistry.getItem("FruitCultivation_CO2").getState() > 10) {
    events.sendCommand('FruitCultivation_Co2Fan', "ON");
}
else {
  events.sendCommand('FruitCultivation_Co2Fan', "OFF");
}
if (itemRegistry.getItem("FruitCultivation_SoilHumidity").getState() <= 30) {
  events.sendCommand('FruitCultivationMQTTThing_FCSprinkler1', "ON");
  events.sendCommand('FruitCultivationMQTTThing_FCSprinkler2', "ON");
}
else {
  events.sendCommand('FruitCultivationMQTTThing_FCSprinkler1', "OFF");
  events.sendCommand('FruitCultivationMQTTThing_FCSprinkler2', "OFF");
}
if (itemRegistry.getItem("FruitCultivation_Temperature").getState() <= 20) {
  events.sendCommand('FruitCultivation_TemperatureSet', "ON");
}
else {
  events.sendCommand('FruitCultivation_TemperatureSet', "OFF");
}



// Potato Cultivation - automatic acting

if (itemRegistry.getItem("PotatoCultivation_CO2").getState() > 10) {
  events.sendCommand('PotatoCultivation_Co2Fan', "ON");
}
else {
  events.sendCommand('PotatoCultivation_Co2Fan', "OFF");
}
if (itemRegistry.getItem("PotatoCultivation_SoilHumidity").getState() <= 30) {
  events.sendCommand('PotatoCultivationMQTTThing_PCSprinkler1', "ON");
  events.sendCommand('PotatoCultivationMQTTThing_PCSprinkler2', "ON");
}
else {
  events.sendCommand('PotatoCultivationMQTTThing_PCSprinkler1', "OFF");
  events.sendCommand('PotatoCultivationMQTTThing_PCSprinkler2', "OFF");
}
if (itemRegistry.getItem("PotatoCultivation_Temperature").getState() <= 20) {
  events.sendCommand('PotatoCultivation_TemperatureSet', "ON");
}
else {
  events.sendCommand('PotatoCultivation_TemperatureSet', "OFF");
}





// Tomato Cultivation - automatic acting

if (itemRegistry.getItem("TomatoCultivation_CO2").getState() > 10) {
    events.sendCommand('TomatoCultivation_Co2Fan', "ON");
  }
  else {
    events.sendCommand('TomatoCultivation_Co2Fan', "OFF");
  }
  if (itemRegistry.getItem("TomatoCultivation_SoilHumidity").getState() <= 30) {
    events.sendCommand('TomatoCultivationMQTTThing_TCSprinkler1', "ON");
    events.sendCommand('TomatoCultivationMQTTThing_TCSprinkler2', "ON");
    events.sendCommand('TomatoCultivationMQTTThing_TCSprinkler3', "ON");
  }
  else {
    events.sendCommand('TomatoCultivationMQTTThing_TCSprinkler1', "OFF");
    events.sendCommand('TomatoCultivationMQTTThing_TCSprinkler2', "OFF");
    events.sendCommand('TomatoCultivationMQTTThing_TCSprinkler3', "OFF");
  }
  if (itemRegistry.getItem("TomatoCultivation_Temperature").getState() <= 20) {
    events.sendCommand('TomatoCultivation_TemperatureSet', "ON");
  }
  else {
    events.sendCommand('TomatoCultivation_TemperatureSet', "OFF");
  }

// Set the optimized parameters for the PotatoCultivation

events.sendCommand('PotatoCultivation_AirHumiditySet', '5');
events.sendCommand('PotatoCultivation_SoilHumiditySet', '5');
events.sendCommand('PotatoCultivation_Light', '5');
